
  //dictation.min.js
  function transcribe(words) {
  	var ifr=document.getElementById('content_ifr');
	var doc=ifr.contentWindow||ifr.contentDocument;
	if (doc.document) doc=doc.document;
    
    var tagbody = doc.getElementById("tinymce");
    tagbody.innerHTML = tagbody.innerHTML + ' ' + words + '.';
    document.getElementById("mic_dictation").value = "";
    doc.getElementById("tinymce").focus();
  }
